(function() {
    angular.module("tbcAngular").controller("LogCtrl", LoginController);

    function LoginController(servicioalumno) {
        var vm = this;
        vm.Init = Init;
        vm.valor=false;
        vm.log=[];
        vm.entrar=false;
        vm.login=login;
        vm.loginActivo=false;
        vm.exitLogin=exitLogin;
        Init();
        function Init() {
        }
        function login() {
            console.log(vm.user +'--'+vm.password);
            servicioalumno.login(vm.user, vm.password).then(function(data) {
                vm.log = data;
                 vm.loginActivo=true;
                 vm.entrar=true;
                console.log(vm.log);
            });
        }
        function exitLogin(){
            vm.loginActivo=false;
            vm.entrar=false;
            console.log("salir");
        }

    }
})();