(function() {
    angular
        .module("tbcAngular")
        .config(function ($stateProvider, $urlRouterProvider) {
           
    $stateProvider
      .state('app', {
        abstract: true,
        views: {
           nav: {
              templateUrl: 'paginas/nav.html'
            },
          nav: {
            templateUrl: 'paginas/nav.html',
            controller: 'LogCtrl as LogCtrl'
          },
          main: {
            templateUrl: 'main.html'
          }
        }
      })
      .state('app.inicio', {
          url: '/home',
          views: {
            article: {
              templateUrl: 'paginas/home.html'
            }
          }
        })
      
        .state('app.alumno', {
        url: '/home',
        views: {
          article: {
            templateUrl: 'paginas/home.html'
          }
        }
      })
     
      .state('app.alumno.mostrar', {
        url: '/mostrarAlumno',
        views: {
          'article@app': {
            templateUrl: 'paginas/MostrarAlumnos.html',
            controller: 'AlumnoCtrl as alumnoCtrl'
          }
        }
      })
      .state('app.alumno.agregar',{
        url: '/agregar', 
        views: {
          'article@app': {
            templateUrl: 'paginas/AgregarAlumnos.html',
            controller: 'AlumnoCtrl as alumnoCtrl'
          }
        }
      })
       
    $urlRouterProvider.otherwise(function ($injector) {
      var $state = $injector.get('$state');
      $state.go('app.alumno');
    });
  })
})();